describe('Mobile testing - FORMS', () => {
  before(async () => {
    //klik menu forms menggunakan accessibility id
    await $('~Forms').click();

    //cek elemen untuk memastikan berada di halaman forms
    const formsPage = await $('~Forms-screen');
    await expect(formsPage).toBeDisplayed();
    await driver.pause(2000);
  });
  it('Verify text displayed is same as inputted', async () => {
    //mengakses input field dan memasukkan value ke dalamnya
    const inputField = await $('~text-input');
    const text = "halo, nama saya LALA";
    await inputField.setValue(text);

    //cek teks di "You have typed" field sesuai dengan yang sudah dimasukkan
    const inputTextResult = await $('~input-text-result');
    await expect(inputTextResult).toHaveText(text);
  });
  it('Verify switch button works well', async () => {
    //kondisi awal sebelum klik switch button
    const switchText = await $('~switch-text').getText();
    await expect(switchText == "Click to turn the switch ON");

    //klik switch button ON
    await $('~switch').click();

    //cek teks berubah setelah klik switch button
    await expect(switchText == "Click to turn the switch OFF");

    //klik switch button OFF
    await $('~switch').click();

    //cek teks berubah setelah klik switch button
    await expect(switchText == "Click to turn the switch ON");
    await driver.pause(2000);
  });
  it('Verify dropdown list showed and user can choose an item', async () => {
    //kondisi awal sebelum klik switch button
    const dropdown = await $('//android.widget.EditText[@resource-id="text_input"]');
    await expect(dropdown).toHaveText("Select an item...");

    //klik dropdown
    await dropdown.click();

    //cek muncul list dropdown
    const dropdownList = await $('//android.widget.ListView[@resource-id="com.wdiodemoapp:id/select_dialog_listview"]');
    await expect(dropdownList).toBeDisplayed();

    //klik salah satu opsi
    await $('//android.widget.CheckedTextView[@resource-id="android:id/text1" and @text="Appium is awesome"]').click();

    //cek opsi berhasil terpilih
    await dropdown.getText();
    await expect(dropdown).toHaveText("Appium is awesome");
    await driver.pause(2000);
  });
});
